import compileSchema from 'ajv-cmd/compile';
import transpileFTL from 'ajv-cmd/ftl';
const ajvDefaults = {
    strict: true,
    coerceTypes: 'array',
    allErrors: true,
    useDefaults: 'empty',
    messages: true
};
export const transpileSchema = (schema, ajvOptions)=>{
    const options = {
        ...ajvDefaults,
        ...ajvOptions
    };
    return compileSchema(schema, options);
};
export const transpileLocale = transpileFTL;

